import {
    AUTH_USER_START,
    AUTH_USER_SUCCESS,
    AUTH_USER_FAILURE
} from 'constants/actionType';

import {authUser as authUserAPI} from 'API/authForm';

export const authForm = (obj, url) => async dispatch => {
    dispatch({type: AUTH_USER_START});
    try {
        const data = await authUserAPI(obj, url);
        dispatch({
            type: AUTH_USER_SUCCESS,
            payload: data
        })
    } catch (err) {
        dispatch({
            type: AUTH_USER_FAILURE,
            payload: err
        })
    }
};