import {
    UPLOAD_FORM_START,
    UPLOAD_FORM_SUCCESS,
    UPLOAD_FORM_FAILURE
} from 'constants/actionType';
import {uploadForm as uploadFormAPI} from 'API/uploadForm';

export const uploadForm = (obj, url) => async dispatch => {
    dispatch ({type: UPLOAD_FORM_START});
    try {
        const data = await uploadFormAPI(obj, url);
        dispatch({
            type:UPLOAD_FORM_SUCCESS,
            payload: data
        })
    } catch (err) {
        dispatch({
            type: UPLOAD_FORM_FAILURE,
            payload: err
        })
    }
};