import React, {Component} from 'react';
import 'grommet-css';

import Layer from 'grommet/components/Layer';
import UploadForm from 'containers/uploadFiles';

class ModalUpload extends Component {


    render() {
        const {showUpload, convertCookie, changeShowUpload} = this.props;
        return (
            <Layer closer={true}
                   hidden={showUpload}
                   overlayClose={true}
                   onClose = {() => changeShowUpload()}>
                <UploadForm props = {this.props} convertCookie = {convertCookie}/>
            </Layer>
        )
    }


}

export default ModalUpload